import React, { Component } from "react";
import { Link } from "react-router-dom";

export default class Calculator extends Component {
    constructor(props) {
      super(props);
        this.balance = this.balance.bind(this);
        this.expense = this.expense.bind(this);;
        this.budget = this.budget.bind(this);
        this.warningBudget = this.warningBudget.bind(this);
        this.warningExpense = this.warningExpense.bind(this);
        this.budgetField = this.budgetField.bind(this);
        this.expenseNameField = this.expenseNameField.bind(this);
        this.expenseValueField = this.expenseValueField.bind(this);
        this.calculateBtn = this.calculateBtn.bind(this);
        this.addExpBtn = this.addExpBtn.bind(this);
        this.expenseList = thist.expenseList.bind(this);

        this.state = {
            list: [],
            amount: {
               budgetAmt: 0,
               expenseAmt: 0,
               balanceAmt: 0
            },
            submitted: false
         };
        }
    }
